def topologicalSort(graph):
    indegrees = {node: 0 for node in graph}
    for edge in graph.edges():
        indegrees[edge.target] += 1

    queue = [node for node in graph.nodes() if indegrees[node] == 0]
    visited = set()

    while queue:
        node = queue.pop(0)
        visited.add(node)

        for edge in graph.edges():
            if edge.target in visited:
                continue

            indegrees[edge.target] -= 1
            if indegrees[edge.target] == 0 and edge.target not in queue:
                queue.append(edge.target)

    if len(visited) != len(graph.nodes()):
        return None

    return list(visited)
